mapper.select:
select 

from user_origin
where id = #{id,jdbcType=BIGINT}
  and user_name = #{userName,jdbcType=VARCHAR}

mapper.delete:
delete from user_origin
where id = #{id,jdbcType=BIGINT}
  and user_name = #{userName,jdbcType=VARCHAR}

mapper.insert:
insert into user_origin (id, user_name, referer_no, 
  raw_add_time, raw_update_time, relation_property, 
  relation_property1, mail, mobile, 
  real_name, cert_no, account_id
  )
values (#{id,jdbcType=BIGINT}, #{userName,jdbcType=VARCHAR}, #{refererNo,jdbcType=VARCHAR}, 
  #{rawAddTime,jdbcType=TIMESTAMP}, #{rawUpdateTime,jdbcType=VARCHAR}, #{relationProperty,jdbcType=VARCHAR}, 
  #{relationProperty1,jdbcType=VARCHAR}, #{mail,jdbcType=VARCHAR}, #{mobile,jdbcType=VARCHAR}, 
  #{realName,jdbcType=VARCHAR}, #{certNo,jdbcType=VARCHAR}, #{accountId,jdbcType=VARCHAR}
  )
